CONSULTA_MONEDERO = "SELECT (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'EUR') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'EUR') AS eur, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'BTC') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'BTC') AS btc, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'ETH') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'ETH') AS eth, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'USDT') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'USDT') AS usdt, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'BNB') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'BNB') AS bnb, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'XRP') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'XRP') AS xrp, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'ADA') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'ADA') AS ada, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'SOL') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'SOL') AS sol, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'DOT') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'DOT') AS dot, (SELECT ifnull(sum (cantidad_to),0) FROM movements WHERE moneda_to = 'MATIC') - (SELECT ifnull(sum (cantidad_from),0) FROM movements WHERE moneda_from = 'MATIC') AS matic;"
CONSULTA_MOVIMIENTOS = "SELECT id, date, time, moneda_from, cantidad_from, moneda_to, cantidad_to, (SELECT ROUND (cantidad_from / cantidad_to, 8)) as PU from movements order by date;"
REGISTRA_COMPRA = "INSERT INTO movements (date, time, moneda_from, cantidad_from, moneda_to, cantidad_to) values (?, ?, ?, ?, ?, ?)"
EUROS_FROM = "SELECT (SELECT sum (cantidad_from) FROM movements WHERE moneda_from = 'EUR') AS eur_inv;"
EUROS_TO = "SELECT (SELECT sum (cantidad_to) FROM movements WHERE moneda_to = 'EUR') AS eur_rec;"